// this is separated because the ACM needs the computation of all moments with order i > 4
// De Rosis Universal (2019) 6th order CMs.
template <>
__device__ __forceinline__
void CM<3, NoAdapter>::apply(float* f, float* f_eq, float* u, float* force, int node) {
    float ux = u[get_vec_index(node, 0)];
    float uy = u[get_vec_index(node, 1)];
    float uz = u[get_vec_index(node, 2)];

    float Fx = force[get_vec_index(node, 0)];
    float Fy = force[get_vec_index(node, 1)];
    float Fz = force[get_vec_index(node, 2)];

    float rho = 0.0f;
    
    float k4 = 0.0f, k5 = 0.0f, k6 = 0.0f, k7 = 0.0f, k8 = 0.0f;
    // float old_f[quadratures];
    const float cs2 = 1.0f / 3.0f;
    const float cs4 = cs2 * cs2;
    
    for (int i = 0; i < quadratures; i++) {
        float cx = C[3*i] - ux;
        float cy = C[3*i+1] - uy;
        float cz = C[3*i+2] - uz;
        float cx2 = cx * cx;
        float cy2 = cy * cy;
        float cz2 = cz * cz;
        float f_i = f[get_node_index(node, i)];

        rho += f[get_node_index(node, i)];
        // old_f[i] = f[get_node_index(node, i)];

        k4 += f_i * cx * cy;
        k5 += f_i * cx * cz;
        k6 += f_i * cy * cz;
        k7 += f_i * (cx2 - cy2);
        k8 += f_i * (cx2 - cz2);
    }

    const float ux2 = ux*ux;
    const float uy2 = uy*uy;
    const float uz2 = uz*uz;
    const float uxuy = ux*uy;
    const float uxuz = ux*uz;
    const float uyuz = uy*uz;
    const float ux2uy = uy*(ux*ux);
    const float uxuy2 = ux*(uy*uy);
    const float ux2uz = uz*(ux*ux);
    const float uxuz2 = ux*(uz*uz);
    const float uy2uz = uz*(uy*uy);
    const float uyuz2 = uy*(uz*uz);
    const float uxuyuz = ux*uy*uz;
    const float ux2uy2 = (ux*ux)*(uy*uy);
    const float ux2uz2 = (ux*ux)*(uz*uz);
    const float uy2uz2 = (uy*uy)*(uz*uz);
    const float ux2uyuz = uy*uz*(ux*ux);
    const float uy2uxuz = ux*uz*(uy*uy);
    const float uz2uxuy = ux*uy*(uz*uz);
    const float ux2uy2uz = uz*(ux*ux)*(uy*uy);
    const float ux2uz2uy = uy*(ux*ux)*(uz*uz);
    const float uy2uz2ux = ux*(uy*uy)*(uz*uz);

    f[get_node_index(node, 0)] = -1.0f/9.0f*Fx*ux - 1.0f/3.0f*Fx*(-2.0f*ux + uxuy2 + uxuz2) - Fx*(ux - uxuy2 - uxuz2 + uy2uz2ux) - 1.0f/9.0f*Fy*uy - 1.0f/3.0f*Fy*(ux2uy - 2.0f*uy + uyuz2) + Fy*(ux2uy - ux2uz2uy - uy + uyuz2) - 1.0f/9.0f*Fz*uz - 1.0f/3.0f*Fz*(ux2uz + uy2uz - 2.0f*uz) - Fz*(ux2uy2uz - ux2uz - uy2uz + uz) - 4.0f*k4*(omega - 1.0f)*(uxuy - uz2uxuy) - 4.0f*k5*(omega - 1.0f)*(uxuz - uy2uxuz) + 4.0f*k6*(omega - 1.0f)*(ux2uyuz - uyuz) + (1.0f/3.0f)*k7*(omega - 1.0f)*(ux2 + ux2uy2 - 2.0f*ux2uz2 - 2.0f*uy2 + uy2uz2 + uz2) + (1.0f/3.0f)*k8*(omega - 1.0f)*(ux2 - 2.0f*ux2uy2 + ux2uz2 + uy2 + uy2uz2 - 2.0f*uz2) - 1.0f/36.0f*rho*(-2.0f*ux2 + uy2 + uz2) - 1.0f/12.0f*rho*(2.0f*ux2 + uy2 + uz2 - 4.0f) - 1.0f/3.0f*rho*(-2.0f*ux2 + ux2uy2 + ux2uz2 - 2.0f*uy2 + uy2uz2 - 2.0f*uz2 + 3.0f) - rho*(ux2 - ux2uy2 + ux2uy2uz*uz - ux2uz2 + uy2 - uy2uz2 + uz2 - 1.0f) - 1.0f/27.0f*rho;
    f[get_node_index(node, 1)] = (1.0f/36.0f)*Fx*(2.0f*ux + 1.0f) + (1.0f/12.0f)*Fx*(-4.0f*ux + 2.0f*uxuy2 + 2.0f*uxuz2 + uy2 + uz2 - 2.0f) + (1.0f/4.0f)*Fx*(2.0f*ux - 2.0f*uxuy2 - 2.0f*uxuz2 - uy2 + uy2uz2 + 2.0f*uy2uz2ux - uz2 + 1.0f) + (1.0f/18.0f)*Fy*uy - 1.0f/2.0f*Fy*(ux2uy - ux2uz2uy + uxuy - uz2uxuy) + (1.0f/6.0f)*Fy*(ux2uy + uxuy - uy + uyuz2) + (1.0f/18.0f)*Fz*uz + (1.0f/2.0f)*Fz*(ux2uy2uz - ux2uz - uxuz + uy2uxuz) + (1.0f/6.0f)*Fz*(ux2uz + uxuz + uy2uz - uz) + k4*(omega - 1.0f)*(2.0f*uxuy + uy - uyuz2 - 2.0f*uz2uxuy) + k5*(omega - 1.0f)*(2.0f*uxuz - 2.0f*uy2uxuz - uy2uz + uz) - 2.0f*k6*(omega - 1.0f)*(ux2uyuz + uxuyuz) - 1.0f/6.0f*k7*(omega - 1.0f)*(ux + ux2 + ux2uy2 - 2.0f*ux2uz2 + uxuy2 - 2.0f*uxuz2 - uy2 + uy2uz2 - uz2 + 1.0f) - 1.0f/6.0f*k8*(omega - 1.0f)*(ux + ux2 - 2.0f*ux2uy2 + ux2uz2 - 2.0f*uxuy2 + uxuz2 - uy2 + uy2uz2 - uz2 + 1.0f) - 1.0f/72.0f*rho*(2.0f*ux + 2.0f*ux2 - uy2 - uz2 + 2.0f) + (1.0f/24.0f)*rho*(2.0f*ux + 2.0f*ux2 + uy2 + uz2 - 2.0f) + (1.0f/2.0f)*rho*(ux + ux2 - ux2uy2 + ux2uy2uz*uz - ux2uz2 - uxuy2 - uxuz2 + uy2uz2ux) + (1.0f/6.0f)*rho*(-2.0f*ux - 2.0f*ux2 + ux2uy2 + ux2uz2 + uxuy2 + uxuz2 - uy2 + uy2uz2 - uz2 + 1.0f) + (1.0f/54.0f)*rho;
    f[get_node_index(node, 2)] = (1.0f/36.0f)*Fx*(2.0f*ux - 1.0f) - 1.0f/12.0f*Fx*(4.0f*ux - 2.0f*uxuy2 - 2.0f*uxuz2 + uy2 + uz2 - 2.0f) + (1.0f/4.0f)*Fx*(2.0f*ux - 2.0f*uxuy2 - 2.0f*uxuz2 + uy2 - uy2uz2 + 2.0f*uy2uz2ux + uz2 - 1.0f) + (1.0f/18.0f)*Fy*uy - 1.0f/2.0f*Fy*(ux2uy - ux2uz2uy - uxuy + uz2uxuy) + (1.0f/6.0f)*Fy*(ux2uy - uxuy - uy + uyuz2) + (1.0f/18.0f)*Fz*uz + (1.0f/2.0f)*Fz*(ux2uy2uz - ux2uz + uxuz - uy2uxuz) + (1.0f/6.0f)*Fz*(ux2uz - uxuz + uy2uz - uz) + k4*(omega - 1.0f)*(2.0f*uxuy - uy + uyuz2 - 2.0f*uz2uxuy) + k5*(omega - 1.0f)*(2.0f*uxuz - 2.0f*uy2uxuz + uy2uz - uz) - 2.0f*k6*(omega - 1.0f)*(ux2uyuz - uxuyuz) + (1.0f/6.0f)*k7*(omega - 1.0f)*(ux - ux2 - ux2uy2 + 2.0f*ux2uz2 + uxuy2 - 2.0f*uxuz2 + uy2 - uy2uz2 + uz2 - 1.0f) + (1.0f/6.0f)*k8*(omega - 1.0f)*(ux - ux2 + 2.0f*ux2uy2 - ux2uz2 - 2.0f*uxuy2 + uxuz2 + uy2 - uy2uz2 + uz2 - 1.0f) + (1.0f/24.0f)*rho*(-2.0f*ux + 2.0f*ux2 + uy2 + uz2 - 2.0f) + (1.0f/72.0f)*rho*(2.0f*ux - 2.0f*ux2 + uy2 + uz2 - 2.0f) - 1.0f/2.0f*rho*(ux - ux2 + ux2uy2 - ux2uy2uz*uz + ux2uz2 - uxuy2 - uxuz2 + uy2uz2ux) + (1.0f/6.0f)*rho*(2.0f*ux - 2.0f*ux2 + ux2uy2 + ux2uz2 - uxuy2 - uxuz2 - uy2 + uy2uz2 - uz2 + 1.0f) + (1.0f/54.0f)*rho;
    f[get_node_index(node, 3)] = (1.0f/18.0f)*Fx*ux + (1.0f/6.0f)*Fx*(-ux + uxuy + uxuy2 + uxuz2) - 1.0f/2.0f*Fx*(uxuy + uxuy2 - uy2uz2ux - uz2uxuy) + (1.0f/36.0f)*Fy*(2.0f*uy + 1.0f) + (1.0f/12.0f)*Fy*(ux2 + 2.0f*ux2uy - 4.0f*uy + 2.0f*uyuz2 + uz2 - 2.0f) - 1.0f/4.0f*Fy*(ux2 + 2.0f*ux2uy - ux2uz2 - 2.0f*ux2uz2uy - 2.0f*uy + 2.0f*uyuz2 + uz2 - 1.0f) + (1.0f/18.0f)*Fz*uz + (1.0f/2.0f)*Fz*(ux2uy2uz + ux2uyuz - uy2uz - uyuz) + (1.0f/6.0f)*Fz*(ux2uz + uy2uz + uyuz - uz) + k4*(omega - 1.0f)*(ux + 2.0f*uxuy - uxuz2 - 2.0f*uz2uxuy) - 2.0f*k5*(omega - 1.0f)*(uxuyuz + uy2uxuz) - k6*(omega - 1.0f)*(2.0f*ux2uyuz + ux2uz - 2.0f*uyuz - uz) - 1.0f/6.0f*k7*(omega - 1.0f)*(2.0f*ux2 + ux2uy + ux2uy2 - 2.0f*ux2uz2 - 2.0f*uy - 2.0f*uy2 + uy2uz2 + uyuz2 + 2.0f*uz2 - 2.0f) - 1.0f/6.0f*k8*(omega - 1.0f)*(-ux2 - 2.0f*ux2uy - 2.0f*ux2uy2 + ux2uz2 + uy + uy2 + uy2uz2 + uyuz2 - uz2 + 1.0f) + (1.0f/72.0f)*rho*(-2.0f*ux2 + uy + uy2 + uz2 + 1.0f) + (1.0f/24.0f)*rho*(2.0f*ux2 + uy + uy2 + uz2 - 3.0f) - 1.0f/2.0f*rho*(ux2uy + ux2uy2 - ux2uy2uz*uz - ux2uz2uy - uy - uy2 + uy2uz2 + uyuz2) + (1.0f/6.0f)*rho*(-ux2 + ux2uy + ux2uy2 + ux2uz2 - 2.0f*uy - 2.0f*uy2 + uy2uz2 + uyuz2 - uz2 + 1.0f) + (1.0f/54.0f)*rho;
    f[get_node_index(node, 4)] = (1.0f/18.0f)*Fx*ux - 1.0f/6.0f*Fx*(ux + uxuy - uxuy2 - uxuz2) + (1.0f/2.0f)*Fx*(uxuy - uxuy2 + uy2uz2ux - uz2uxuy) + (1.0f/36.0f)*Fy*(2.0f*uy - 1.0f) - 1.0f/12.0f*Fy*(ux2 - 2.0f*ux2uy + 4.0f*uy - 2.0f*uyuz2 + uz2 - 2.0f) + (1.0f/4.0f)*Fy*(ux2 - 2.0f*ux2uy - ux2uz2 + 2.0f*ux2uz2uy + 2.0f*uy - 2.0f*uyuz2 + uz2 - 1.0f) + (1.0f/18.0f)*Fz*uz + (1.0f/2.0f)*Fz*(ux2uy2uz - ux2uyuz - uy2uz + uyuz) + (1.0f/6.0f)*Fz*(ux2uz + uy2uz - uyuz - uz) - k4*(omega - 1.0f)*(ux - 2.0f*uxuy - uxuz2 + 2.0f*uz2uxuy) + 2.0f*k5*(omega - 1.0f)*(uxuyuz - uy2uxuz) - k6*(omega - 1.0f)*(2.0f*ux2uyuz - ux2uz - 2.0f*uyuz + uz) - 1.0f/6.0f*k7*(omega - 1.0f)*(2.0f*ux2 - ux2uy + ux2uy2 - 2.0f*ux2uz2 + 2.0f*uy - 2.0f*uy2 + uy2uz2 - uyuz2 + 2.0f*uz2 - 2.0f) + (1.0f/6.0f)*k8*(omega - 1.0f)*(ux2 - 2.0f*ux2uy + 2.0f*ux2uy2 - ux2uz2 + uy - uy2 - uy2uz2 + uyuz2 + uz2 - 1.0f) + (1.0f/72.0f)*rho*(-2.0f*ux2 - uy + uy2 + uz2 + 1.0f) + (1.0f/24.0f)*rho*(2.0f*ux2 - uy + uy2 + uz2 - 3.0f) + (1.0f/2.0f)*rho*(ux2uy - ux2uy2 + ux2uy2uz*uz - ux2uz2uy - uy + uy2 - uy2uz2 + uyuz2) - 1.0f/6.0f*rho*(ux2 + ux2uy - ux2uy2 - ux2uz2 - 2.0f*uy + 2.0f*uy2 - uy2uz2 + uyuz2 + uz2 - 1.0f) + (1.0f/54.0f)*rho;
    f[get_node_index(node, 5)] = (1.0f/18.0f)*Fx*ux + (1.0f/6.0f)*Fx*(-ux + uxuy2 + uxuz + uxuz2) - 1.0f/2.0f*Fx*(uxuz + uxuz2 - uy2uxuz - uy2uz2ux) + (1.0f/18.0f)*Fy*uy + (1.0f/6.0f)*Fy*(ux2uy - uy + uyuz + uyuz2) + (1.0f/2.0f)*Fy*(ux2uyuz + ux2uz2uy - uyuz - uyuz2) + (1.0f/36.0f)*Fz*(2.0f*uz + 1.0f) + (1.0f/12.0f)*Fz*(ux2 + 2.0f*ux2uz + uy2 + 2.0f*uy2uz - 4.0f*uz - 2.0f) - 1.0f/4.0f*Fz*(ux2 - ux2uy2 - 2.0f*ux2uy2uz + 2.0f*ux2uz + uy2 + 2.0f*uy2uz - 2.0f*uz - 1.0f) - 2.0f*k4*(omega - 1.0f)*(uxuyuz + uz2uxuy) + k5*(omega - 1.0f)*(ux - uxuy2 + 2.0f*uxuz - 2.0f*uy2uxuz) - k6*(omega - 1.0f)*(ux2uy + 2.0f*ux2uyuz - uy - 2.0f*uyuz) - 1.0f/6.0f*k7*(omega - 1.0f)*(-ux2 + ux2uy2 - 2.0f*ux2uz - 2.0f*ux2uz2 - uy2 + uy2uz + uy2uz2 + uz + uz2 + 1.0f) - 1.0f/6.0f*k8*(omega - 1.0f)*(2.0f*ux2 - 2.0f*ux2uy2 + ux2uz + ux2uz2 + 2.0f*uy2 + uy2uz + uy2uz2 - 2.0f*uz - 2.0f*uz2 - 2.0f) + (1.0f/72.0f)*rho*(-2.0f*ux2 + uy2 + uz + uz2 + 1.0f) + (1.0f/24.0f)*rho*(2.0f*ux2 + uy2 + uz + uz2 - 3.0f) + (1.0f/2.0f)*rho*(ux2uy2uz*uz + ux2uy2uz - ux2uz - ux2uz2 - uy2uz - uy2uz2 + uz + uz2) + (1.0f/6.0f)*rho*(-ux2 + ux2uy2 + ux2uz + ux2uz2 - uy2 + uy2uz + uy2uz2 - 2.0f*uz - 2.0f*uz2 + 1.0f) + (1.0f/54.0f)*rho;
